class Camiseta:
    def __init__(self):
        self._tamanho = ""

    def getTamanho(self):
        return self._tamanho

    def setTamanho(self, valor: str):
        tamanhos_validos = ["PP", "P", "M",  "G", "GG", "XG"]
        valor = valor.upper()
        valor = tamanhos_validos
        if valor in tamanhos_validos:
            self._tamanho = valor 
        else:
            print(" Esse tamanho não é permitido, os tamanhos validos são PP, P, M, G, GG, XG")


roupa = Camiseta()
roupa.setTamanho("M")

print(" Parabéns! Você comprou uma roupa tamanho", roupa.getTamanho())
